/**
 * This script loads the local projects.json file (generated by updateProjectsJson.js)
 * and dynamically populates the #projects-container element with each project.
 */
async function fetchProjects() {
  try {
    const response = await fetch('data/projects.json');
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    const projects = await response.json();
    const container = document.getElementById('projects-container');
    container.innerHTML = '';

    projects.forEach(project => {
      const projectLink = document.createElement('a');
      projectLink.href = project.url;
      projectLink.target = '_blank';
      projectLink.className = 'project-item';
      projectLink.innerHTML = `
        <h3>${project.name}</h3>
        <p>${project.description}</p>
      `;
      container.appendChild(projectLink);
    });
  } catch (error) {
    console.error('Error fetching projects:', error);
    const container = document.getElementById('projects-container');
    container.innerHTML = '<p>Failed to load projects. Please try again later.</p>';
  }
}

document.addEventListener('DOMContentLoaded', fetchProjects);
